import React from 'react';
import { connect } from 'react-redux';

import Navbar from '../layouts/navbar';
import { signUp  } from '../../actions/users';
import Notification from '../layouts/notification';


class Sign_up extends React.Component {
  constructor() {
    super();
    this.state = {
      user: {
        email: '',
        username: '',
        firstname: '',
        lastname: '',
        password: '',
        password_confirmation: ''
      }
    };
  }

  handleChange(field, e) {
    let new_user = Object.assign({}, this.state.user);
    new_user[field] = e.target.value;
    this.setState({ user: new_user });
  }

  handleSubmit(element) {
    element.preventDefault();
    this.props.onSignUp(this.state.user)
  }

  render() {
    return (
      <div>

        <Navbar />

        <Notification />

        <div className='col-sm-3 col-md-4' />

        <div className='col-sm-6 col-md-4' >

          <h2 className='text-center'>Sign up form</h2>

          <form className='form-horizontal' onSubmit={this.handleSubmit.bind(this)} >

            <div>
              <label>Email:</label>
              <input
                className='form-control'
                placeholder="Enter your email"
                type="email"
                onChange={this.handleChange.bind(this, 'email')}
                minLength="8"
                required
              />
            </div>

            <div>
              <label>Username:</label>
              <input
                className='form-control'
                placeholder="Enter a username"
                type="text"
                onChange={this.handleChange.bind(this, 'username')}
                pattern='[A-Za-z\d]{3,}'
                title="Username must be unique and can contain only letters and numbers. 3-10 symbols is allowed"
                required
              />
            </div>

            <div>
              <label>Firstname:</label>
              <input
                className='form-control'
                placeholder="Enter your firstname"
                type="text"
                onChange={this.handleChange.bind(this, 'firstname')}
                pattern='[A-Za-z]{3,10}'
                title="Firstname can contain only letters. 3-10 symbols is allowed"
                // minLength="3"
                required
              />
            </div>

            <div>
              <label>Lastname:</label>
              <input
                className='form-control'
                placeholder="Enter your lastname"
                type="text"
                onChange={this.handleChange.bind(this, 'lastname')}
                pattern='[A-Za-z]'
                title="Lastname can contain only letters. 4-12 symbols is allowed"
                pattern='[A-Za-z]{4,12}'
                required
              />
            </div>

            <div>
              <label>Password:</label>
              <input
                className='form-control'
                placeholder="Enter a password"
                type="password"
                onChange={this.handleChange.bind(this, 'password')}
                minLength="6"
                required
              />
            </div>

            <div>
              <label>Password confirmation:</label>
              <input
                className='form-control'
                placeholder="Enter a password"
                type="password"
                onChange={this.handleChange.bind(this, 'password_confirmation')}
                minLength="6"
                required
              />
            </div>

            <br />

            <button type="submit" className="btn btn-primary btn-lg center-block">Sign up</button>

          </form>
        </div>

      </div>
    );

  }
}

export default connect(
  state => ({}),
  dispatch => ({
    onSignUp: (user) => {
      dispatch(signUp(user));
    }
  })
)(Sign_up);

import React, { Component } from 'react';
import { connect } from 'react-redux';
import { Link } from 'react-router';

// import { sortTasks } from '../middlewares/sorted_tasks';
import {
  getTasks,
  deleteTask,
  completeTask,
  destroyCheckedTasks
} from '../../actions/tasks';


class TasksList extends Component {
  constructor(props) {
    super(props);
    this.state = {
      tasks: [],
      checked: []
    }
  }

  //перевіряє чи зі стор  прийшли в пропси
  static contextTypes = {
    store: React.PropTypes.object
  };

  componentDidMount () {
    this.context.store.dispatch(getTasks());
  }

  // //замінили на componentDidMount(аналогія в tasks_edition)
  componentWillReceiveProps(nextProps) {
    this.setState({ tasks: nextProps.tasks });
  }

  handleComplete (id, active) {
    this.props.onCompleteTask(id, active);
  }

  handleDestroy (id) {
    this.props.onDestroyTask(id);
  }

  sortTasks (type) {
    if (type === 'title') {
      let sortedTasks = this.props.tasks.sort(function (a, b) {
        if (a.title > b.title) {
          return 1;
        }
        if (a.title < b.title) {
          return -1;
        }
        // a должно быть равным b
        return 0;
      });
      this.setState({ tasks: sortedTasks });
      // this.props.onSortTasks(tasks);

    } else {
      let sortedTasks = this.props.tasks.sort(function (a, b) {
        if (a.priority > b.priority) {
          return 1;
        }
        if (a.priority < b.priority) {
          return -1;
        }
        // a должно быть равным b
        return 0;
      });
      this.setState({ tasks: sortedTasks });
      // this.props.onSortTasks(tasks);
    }
  }

  handleCheck (id) {
    let searchResult = this.state.checked.indexOf(id);
    if (searchResult !== -1) {
      //удалить ід з масива чекд (починаючи з індекс, к-сть ел)
      this.state.checked.splice(searchResult, 1);
    } else {
      this.state.checked.push(id);
    }

    this.setState({ checked: this.state.checked });
  }

  handleDeleteChecked () {
    if (this.state.checked.length > 0) {
      this.props.onDestroyCheckedTasks(this.state.checked);
    }
  }

  handleCheckAll () {
    if (this.props.tasks.length === this.state.checked.length) {
      this.setState({ checked: [] });
    } else {
      let fakeState = [];
      for (var i = 0; i < this.props.tasks.length; i++) {
        fakeState.push(this.props.tasks[i].id)
      }
      this.setState({ checked: fakeState });
    }
  }

  checkboxRendering (id) {
    let searchResult = this.state.checked.indexOf(id);
    if (searchResult === -1) {
      return false
    } else {
      return true
    }
  }

  checkboxIcon () {
    if (this.props.tasks.length === this.state.checked.length) {
      return (
        'glyphicon-unchecked'
      );
    } else {
      return (
        'glyphicon-check'
      );
    }
  }

  render () {
    return (
      <div>
        <div className='container-fluid'>
          <div className={`glyphicon btn-group btn ${this.checkboxIcon()}`} onClick={this.handleCheckAll.bind(this)} />

          <div className='btn-group btn pull-right'>
            <div className="btn btn-group btn-info" onClick={this.sortTasks.bind(this, 'priority')}>
              Sort by priority
            </div>
          </div>

          <div className='btn-group btn pull-right'>
            <div className="btn btn-group btn-info" onClick={this.sortTasks.bind(this, 'title')}>
              Sort by asc
            </div>
          </div>

          <div className='btn-group btn pull-right'>
            <div className="btn btn-group btn-danger"onClick={ this.handleDeleteChecked.bind(this) } >
              Delete checked
            </div>
          </div>
        </div>

        <h3> Current tasks: </h3>

        {this.props.tasks.map( (task) => {
          if (!task.active) {

            return (
              <div key={task.id} className='container-fluid'>

                <div className='col-xs-1'>
                  <input type='checkbox' onChange={this.handleCheck.bind(this, task.id)} checked={this.checkboxRendering(task.id)} />
                </div>
                <div className="li_height hover1 for_icons">

                  <Link to={`/tasks/${task.id}/profile`} className='task_list' >
                    <div className="col-xs-7">{ task.title }</div>
                  </Link>

                  <div className="col-xs-1 priority">{ task.priority }</div>
                  <div className="col-xs-3 hover2 icons-block" >

                    <Link to={`/tasks/${task.id}/edit`} >
                      <span className="glyphicon glyphicon-pencil" title="Edit task"></span>
                    </Link>

                    <span onClick={this.handleDestroy.bind(this, task.id)} className="glyphicon glyphicon-trash" title="Delete task"></span>

                    <span onClick={this.handleComplete.bind(this, task.id, task.active)} className="glyphicon glyphicon-ok" title="Mark as completed"></span>

                  </div>
                </div>
              </div>
            );

          }
        })}

        <h3> Completed tasks: </h3>
        {this.props.tasks.map( (task) => {
          if (task.active) {

            return (
              <div key={task.id} className='container-fluid'>

                <div className='col-xs-1'>
                  <input type='checkbox' onChange={this.handleCheck.bind(this, task.id)} checked={this.checkboxRendering(task.id)} />
                </div>

                <div className="li_height hover1 for_icons">

                  <Link to={`/tasks/${task.id}/profile`} className='task_list' >
                    <div className="col-xs-7">{ task.title }</div>
                  </Link>

                  <div className="col-xs-1 priority">{ task.priority }</div>
                  <div className="col-xs-3 hover2 icons-block">

                    <Link to={`/tasks/${task.id}/edit`} >
                      <span className="glyphicon glyphicon-pencil" title="Edit task"></span>
                    </Link>

                    <span onClick={this.handleDestroy.bind(this, task.id)} className="glyphicon glyphicon-trash" title="Delete task"></span>

                    <span onClick={this.handleComplete.bind(this, task.id, task.active)} className="glyphicon glyphicon-remove" title="Mark as uncompleted"></span>

                  </div>
                </div>
              </div>
            );

          }
        })}

      </div>
    );
  }

}


export default connect(
  state => ({
    tasks: state.tasks.items
  }),
  dispatch => ({

    onDestroyTask: (id) => {
      dispatch(deleteTask(id));
    },

    onCompleteTask: (id, active) => {
      dispatch(completeTask(id, active));
    },

    // onSortTasks: (tasks) => {
    //   dispatch(sortTasks(tasks));
    // },

    onDestroyCheckedTasks: (ids) => {
      dispatch(destroyCheckedTasks(ids));
    }
  })
)(TasksList);

